mapper.select:
select count(*)
from widget
where empl_no = #{emplNo}

mapper.insert:
insert into widget(
	widg_no
	, empl_no
	, widg_x
	, widg_y
	, widg_w
	, widg_h
	, widg_code
)values(
	seq_widget.nextval
	, #{emplNo}
	, #{widgX}
	, #{widgY}
	, #{widgW}
	, #{widgH}
	, #{widgCode}
)

mapper.update:
update widget
set 
	widg_x = #{widgX}
	, widg_y = #{widgY}
	, widg_w = #{widgW}
	, widg_h = #{widgH}
where empl_no = #{emplNo}
and widg_code = #{widgCode}

update widget
set
	use_yn = #{useYn}
where empl_no = #{emplNo}
and widg_code = #{widgCode}
