mapper.select:

SELECT ERNT_NO, EMPL_NO, EQPNM_CD, RGSDE, ERNT_BEGIN_DT, ERNT_END_DT, RESOURCE_TYPE 
FROM EQUIPMENT_RENTAL
WHERE EQPNM_CD = #{eqpnmCd}
  AND (
      ERNT_BEGIN_DT < #{erntEndDt,jdbcType=VARCHAR} AND ERNT_END_DT > #{erntBeginDt,jdbcType=VARCHAR}
  )

mapper.update:
UPDATE EQUIPMENT_RENTAL
SET PRMISN_YN = 'Y'
WHERE ERNT_NO = #{erntNo}

UPDATE EQUIPMENT_RENTAL
SET PRMISN_YN = 'N'
WHERE ERNT_NO = #{erntNo}

mapper.select:
WITH all_hours AS ( SELECT TO_CHAR(LEVEL - 1, 'FM00') AS hour_of_day FROM DUAL
CONNECT BY LEVEL <= 25
) SELECT ah.hour_of_day, COALESCE(rental_count, 0) AS count FROM all_hours ah LEFT JOIN ( SELECT TO_CHAR(ernt_begin_dt, 'HH24') AS hour_of_day, COUNT(*) AS rental_count FROM equipment_rental GROUP BY TO_CHAR(ernt_begin_dt, 'HH24') ) rental_data ON ah.hour_of_day = rental_data.hour_of_day ORDER BY ah.hour_of_day
