mapper.select:
SELECT task_no, upper_task_no, prjct_no, empl_no, task_nm, task_cn, task_progrs, task_imprtnc, task_priort, task_register, task_rgsde, task_begin_dt, task_end_dt, task_last_updde, atch_file_group_no FROM task where prjct_no = #{prjctNo}
and upper_task_no is null
and upper_task_no = #{upperTaskNo}

mapper.update:
update task
set task_progrs = #{taskProgrs}
where task_no = #{taskNo}

mapper.select:
SELECT round((COUNT(*) - SUM( CASE
WHEN task_end_dt <= current_date THEN
1 ELSE 0 END )) / COUNT(*) * 100, 1) AS rate FROM task WHERE empl_no = #{emplNo} AND prjct_no = #{prjctNo}
